mapper.select:
SELECT EN_NAME as label,bcch as bcch,BSIC as bsic from (
 select DISTINCT c1.EN_NAME, c1.bcch , c1.BSIC from RNO_GSM_CELL c1, RNO_GSM_CELL c2
    where
         c1.EN_NAME <> c2.EN_NAME and c1.CELL_ID <> c2.CELL_ID 
        and c1.BCCH =c2.BCCH
          and c1.BSIC = c2.BSIC
          and c1.AREA_ID = #{cityId}
        and c2.AREA_ID = #{cityId}
 ORDER BY  c1.bcch,c1.BSIC)
where BCCH = #{bcch} and BSIC = #{bsic}

select a.id,a.name as name, to_char(a.create_time,'YYYY-MM-DD HH24:mi:ss') as createTime, b.name as areaName from RNO_GSM_CO_BSIC_DESCRIPTOR a, RNO_GSM_SYS_AREA b where a.AREA_ID = b.AREA_ID and a.AREA_ID in (select AREA_ID from RNO_GSM_SYS_AREA where PARENT_ID = #{cityId})
and a.name like #{schemaName}
order by a.CREATE_TIME DESC
